const nationality = ["Australian", "American", "Mexican", "Japanese", "Chinese", "South Korean", "North Korean"]
const country = ["Australia", "America", "Mexico", "Japan", "Chinese", "South Korea", "North Korea"]

function makeFirstName() {
    let index = Math.floor(Math.random()*firstname.length);
    return firstname[index];
}

function makeMiddleName() {
    let index = Math.floor(Math.random()*middlename.length);
    return middlename[index];
}

function makeLastName() {
    let index = Math.floor(Math.random()*lastname.length);
    return lastname[index];
}

function makePassportNumber() {
        let num = generateRandomString(10,true,true);
        return num;
}

function generateRandomString(length, numbers, letters) {
    let strArray = []
    if(numbers==true && letters==true){
        for(i=0;i<length;i++){
            if(Math.random()>=0.5){
                let characCode = Math.floor(Math.random()*5);
                strArray.push(characters[characCode]);
            }
            else{
                let number = Math.floor(Math.random()*10)
                strArray.push(number);
            }
        }
    }
    return(strArray.join(""));
}

function makeResidencyStatus() {
    if(Math.floor(Math.random()*2) == 1){
        return true;
    }
    else{
        return false;
    }
}

function makeNationality() {
    let index = Math.floor(Math.random()*nationality.length);
    return nationality[index];
}

function makeCountry() {
    let index = Math.floor(Math.random()*country.length);
    return country[index];
}

function setPassportPhoto() {
    let index = Math.floor(Math.random()*7) + 1
    let filename = "assets/passport-photos/" + index + ".png";
    document.getElementById("passportphoto").src = filename;
    sessionStorage.setItem("imgcode", index);
}

function startGame() {
    cleanseFields()
    sessionStorage.setItem("a",false);
    $("#contentstart").hide();
    $("#contentstop").fadeIn();
    document.getElementById("gamearea-bottom-center").style = "background-color:none;"
    document.getElementById("gamearea-bottom-right").style = "background-color:none;"

    let passnum = makePassportNumber()
    document.getElementById("number-passport").innerHTML = passnum;

    let firstname = makeFirstName();
    document.getElementById("firstname-passport").innerHTML = firstname;

    let middlename = makeMiddleName();
    document.getElementById("middlename-passport").innerHTML = middlename;

    let lastname = makeMiddleName();
    document.getElementById("lastname-passport").innerHTML = lastname;

    let nationality = makeNationality();
    document.getElementById("nationality-passport").innerHTML = nationality;

    let country = makeCountry();
    document.getElementById("residence-passport").innerHTML = country;

    setPassportPhoto();

    let resident = makeResidencyStatus();

    if(resident == true){
        document.getElementById("gamearea-bottom-center").style = "background-color:darkgrey;"
        if(Math.random()>0.5){
            makeMistake(true);
        }
        else{
            populateNoMistake(true);
        }
    }
    else if (resident == false){
        document.getElementById("gamearea-bottom-right").style = "background-color:darkgrey;"
        if(Math.random()>0.5){
            makeMistake(false);
        }
        else{
            populateNoMistake(false);
        }
    }
}

function makeMistake(isResident){
    if (isResident == true) {
        let rand = Math.random()
        if(rand<0.2){
            let first = makeFirstName()
            document.getElementById("firstname-id").innerHTML = first;
            sessionStorage.setItem("a",true);
            document.getElementById("middlename-id").innerHTML = document.getElementById("middlename-passport").innerHTML;
            document.getElementById("lastname-id").innerHTML = document.getElementById("lastname-passport").innerHTML;
            document.getElementById("idphoto").src = "assets/passport-photos/" + sessionStorage.getItem("imgcode") + ".png";
        }
        else if(rand<0.4){
            let middle = makeMiddleName()
            document.getElementById("middlename-id").innerHTML = middle;
            sessionStorage.setItem("a",true);
            document.getElementById("firstname-id").innerHTML = document.getElementById("firstname-passport").innerHTML;
            document.getElementById("lastname-id").innerHTML = document.getElementById("lastname-passport").innerHTML;
            document.getElementById("idphoto").src = "assets/passport-photos/" + sessionStorage.getItem("imgcode") + ".png";
            
        }
        else if(rand<0.6){
            let last = makeLastName()
            document.getElementById("lastname-id").innerHTML = last;
            sessionStorage.setItem("a",true);
            document.getElementById("middlename-id").innerHTML = document.getElementById("middlename-passport").innerHTML;
            document.getElementById("firstname-id").innerHTML = document.getElementById("firstname-passport").innerHTML;
            document.getElementById("idphoto").src = "assets/passport-photos/" + sessionStorage.getItem("imgcode") + ".png";
        }
        else{
            let index = sessionStorage.getItem("imgcode");
            while(index == sessionStorage.getItem("imgcode")) {
                index = Math.floor(Math.random()*7) + 1
            }
            let filename = "assets/passport-photos/" + index + ".png";
            document.getElementById("idphoto").src = filename;
            sessionStorage.setItem("a",true);
        }
    }

    if(isResident==false){
        let rand = Math.random()
        if(rand<0.2){
            let first = makeFirstName()
            document.getElementById("firstname-visa").innerHTML = first;
            sessionStorage.setItem("a",true);
        }
        else if(rand<0.4){
            let middle = makeMiddleName()
            document.getElementById("middlename-visa").innerHTML = middle;
            sessionStorage.setItem("a",true);
        }
        else if(rand<0.6){
            let last = makeLastName()
            document.getElementById("lastname-visa").innerHTML = last;
            sessionStorage.setItem("a",true);
        }
        else{
            let index = sessionStorage.getItem("imgcode");
            while(index == sessionStorage.getItem("imgcode")) {
                index = Math.floor(Math.random()*7) + 1
            }
            let filename = "assets/passport-photos/" + index + ".png";
            document.getElementById("visaphoto").src = filename;
            sessionStorage.setItem("a",true);
        }
    }
}

function cleanseFields() {
    document.getElementById("firstname-passport").innerHTML = "";
    document.getElementById("middlename-passport").innerHTML = "";
    document.getElementById("lastname-passport").innerHTML = "";
    document.getElementById("nationality-passport").innerHTML = "";
    document.getElementById("residence-passport").innerHTML = "";
    document.getElementById("number-passport").innerHTML = "";
    document.getElementById("firstname-visa").innerHTML = "";
    document.getElementById("middlename-visa").innerHTML = "";
    document.getElementById("lastname-visa").innerHTML = "";
    document.getElementById("nationality-visa").innerHTML = "";
    document.getElementById("valid-visa").innerHTML = "";
    document.getElementById("passportnumber-visa").innerHTML = "";
    document.getElementById("firstname-id").innerHTML = "";
    document.getElementById("middlename-id").innerHTML = "";
    document.getElementById("lastname-id").innerHTML = "";
    document.getElementById("expiry-id").innerHTML = ""

    document.getElementById("visaphoto").src = "assets/blank-person.png";
    document.getElementById("idphoto").src = "assets/blank-person.png";
    document.getElementById("passportphoto").src = "assets/blank-person.png";
    

}

function populateNoMistake(isResident){
    if(isResident===true){
        document.getElementById("firstname-id").innerHTML = document.getElementById("firstname-passport").innerHTML;
        document.getElementById("middlename-id").innerHTML = document.getElementById("middlename-passport").innerHTML;
        document.getElementById("lastname-id").innerHTML = document.getElementById("lastname-passport").innerHTML;
    }
    else if(isResident===false){
        document.getElementById("firstname-visa").innerHTML = document.getElementById("firstname-passport").innerHTML;
        document.getElementById("middlename-visa").innerHTML = document.getElementById("middlename-passport").innerHTML;
        document.getElementById("lastname-visa").innerHTML = document.getElementById("lastname-passport").innerHTML;
    }
    else{
        console.log("Critical Error: function populateNoMistake was passed an incorrect parameter.");
        startGame()
        return;
    }
}